public class A_While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=0;
		
		 while (i<10) 
		 // i의 값이 10보다 작으면 true / 10보다 크다면 false
			 			{
	            System.out.println("Coding Everybody"+i);
		// i의 값에 1을 더한다.
	            i++;
			 			} while(i<=20) {
			 				System.out.println("Coding"+i);
			 				
			 				i++;
			 				
			 			}
/**	while (조건_언제까지) {
 	반복실행 영역
    
 **/
		 
		 
	}
}
