package com.jykdream.java;

public class number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		// int = 데이터 type 숫자형식_정수!!! / a = 변수의 이름!!  즉, 이 문장은 변수선언 을 한 문장이다.
		
		a=1;
		// a라는 변수의 값을 1로 준것이다.
		System.out.println(a+1);
		
		double b;
		// double = 실수(소숫점)의 데이터 type
		
		b=1.1;
		
		System.out.println(b);
		
// 정리!!!   숫자의 데이터type 은  정수: int   /  실수: double 를 사용한다!!
	}

}
