package com.jykdream.java.operator;

public class 산술연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
// 산술연산자 는 수학적인 계산에 사용되는 연산자!!
		
// '/' = 나누기, '%' 나머지, '*' 곱하기, '+' 더하기, '-' 빼기
	
	// 결과 값 = 3
	double result = 1 + 2;
	System.out.println(result);
	
	// 결과 값 = 2
	result = result -1;
	System.out.println(result);
	
	// 결과 값 = 4
	result = result *2;
	System.out.println(result);
	
	
	// 결과 값 = 2
	result = result/2;
	System.out.println(result);
	
	// 결과 값 = 10
	result = result*5;
	System.out.println(result);
	
	// 결과 값 = 1
	result = result % 3;
	System.out.println(result);
	
	result = result % 3;
	System.out.println(result);
	
	// 문자와 문자를 연결할때 '+' 연산자를 사용해도 된다.	
    String first = "This is";
    String second = " a boy";
    String third = first + second;
    System.out.println(third);
	
	}

}
