package com.jykdream.java;

public class 형변환 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

  /** int 타입을 -> double or float 로 변환 시킬때 **/
		
	double a = 3.0F;
	 
	
	 double f = 3.0;
	 
/** byte -> short  -> int -> long -> float -> double
       형변환 원칙!! 즉, 정수 -> 실수
 */
	 
	 int z = 3;
	 float x = 1.0F;
	 float v = z + x;
	 
	 System.out.println(v);
	 
	
	
		
		
		
		
		
	}

}
